import { Link } from "react-router-dom";

import { useParams } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import { deleteRecipe } from "../../services/JsonServerClient";
function RecipeList({ recipes, query, title }) {
  const { id } = useParams();
  // function for moving the other page
  const navigate = useNavigate();

  // delete recipe function
  const handleDelete = async () => {
    // eslint-disable-next-line no-restricted-globals
    if (confirm("Do you want to delete this recipe")) {
      try {
        await deleteRecipe(id);
        console.log("New Recipe Deleted");
        navigate("/");
      } catch (error) {
        console.error("Error delete recipe:", error);
      }
    } else {
      navigate("/");
    }
  };

  return (
    <div className="recipe-list">
      <h2>{title}</h2>
      {recipes

        // eslint-disable-next-line array-callback-return
        .filter((post) => {
          // if search is empty, then return all recipes, else return filtered data
          if (query === "") {
            return post;
          } else if (
            post.category.toLowerCase().includes(query.toLowerCase())
          ) {
            return post;
          } else if (post.dish.toLowerCase().includes(query.toLowerCase())) {
            return post;
          } else if (post.country.toLowerCase().includes(query.toLowerCase())) {
            return post;
          } else if (
            post.ingredient.toLowerCase().includes(query.toLowerCase())
          ) {
            return post;
          }
        })

        .map((recipe) => (
          // map is function like loop, to get all infromation of filtered data, and have link for each recipe
          <div className="recipe-preview" key={recipe.id}>
            <Link to={`/recipes/${recipe.id}`}>
              <h2>{recipe.dish}</h2>
            </Link>
            <button onClick={handleDelete}>delete</button>
          </div>
        ))}
    </div>
  );
}

export default RecipeList;
