import { useState, useEffect } from "react";
import { getRecipes, getRecipeDetails } from "../services/JsonServerClient";

const useFetch = () => {
  const [data, setData] = useState(null);

  useEffect(() => {
    async function fetchData() {
      try {
        const received_data = await getRecipes(); // Call the imported function to get json data
        setData(received_data);
      } catch (e) {
        alert(e.message);
      }
    }

    fetchData();
  }, []);

  return { data };
};

const useFetchDetail = () => {
  const [data, setData] = useState(null);

  useEffect(() => {
    async function fetchData() {
      try {
        const received_data = (await getRecipeDetails()).json; // Call the imported function to get json data
        const the_id = (await getRecipeDetails()).id;
        setData(received_data);
      } catch (e) {
        alert(e.message);
      }
    }

    fetchData();
  }, []);

  return { the_id, data };
};

export { useFetch, useFetchDetail };
