import { useState, useEffect } from "react";

import RecipeList from "./RecipeList";
import { getRecipes } from "../../services/JsonServerClient";
//import useFetch from "../../hooks/useFetch";
import SearchBar from "./SearchBar";

const Home = () => {
  const [data, setData] = useState(null);
  // get data from backend json-server
  useEffect(() => {
    async function fetchData() {
      try {
        const received_data = await getRecipes(); // Call the imported function to get json data
        setData(received_data);
      } catch (e) {
        alert(e.message);
      }
    }

    fetchData();
  }, []);

  // search bar
  const [query, setQuery] = useState("");

  return (
    <div className="home">
      <SearchBar query={query} setQuery={setQuery} />
      {data && <RecipeList recipes={data} query={query} title="Recipes List" />}
    </div>
  );
};

export default Home;
