import { useState, useEffect } from "react";

// get all recipes
const getRecipes = async () => {
  try {
    const response = await fetch("http://localhost:8000/recipes/");
    const json = await response.json();
    return json;
  } catch (e) {
    alert(e.message);
  }
};

//get recipes details
function getRecipeDetails(id) {
  const [data, setData] = useState(null);

  useEffect(() => {
    getData();
  }, []);

  async function getData() {
    await fetch("http://localhost:8000/recipes/" + id)
      .then((response) => {
        setData(response.data);
      })
      .catch((error) => {
        alert(error);
      });
  }
}

// get a recipe fetch method

// create a recipe fetch method
const createRecipe = async (recipe) => {
  try {
    const response = await fetch("http://localhost:8000/recipes", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(recipe),
    });

    if (!response.ok) {
      throw new Error("Failed to add recipe");
    }
  } catch (error) {
    throw error;
  }
};

//update recipe fetch method

//delete recipe fetch method
const deleteRecipe = async (id) => {
  try {
    const response = await fetch("http://localhost:8000/recipes/" + id, {
      method: "DELETE",
    });
    if (!response.ok) {
      throw new Error("Failed to delete recipe");
    }
  } catch (error) {
    throw error;
  }
};

export { getRecipes, getRecipeDetails, createRecipe, deleteRecipe };
